pageContext.rootQuery:
query($uid: String!) {
  prismic {
    allBlog_articles(uid: $uid) {
      edges {
        node {
          title
          subtitle
          authored_date
          body {
            ... on PRISMIC_Blog_articleBodyArticle_content {
              type
              label
              primary {
                rich_text
              }
            }
            ... on PRISMIC_Blog_articleBodyHorizontal_rule {
              type
              label
            }
            ... on PRISMIC_Blog_articleBodyBlockquote {
              type
              primary {
                text
              }
            }
            ... on PRISMIC_Blog_articleBodyMedia {
              type
              label
            }
          }
        }
      }
    }
  }
}
